import React, { useState, useContext, useEffect } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";

export default function Register() {
  const { dispatch } = useContext(StateContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [passwordRepeat, setPasswordRepeat] = useState("");

  const [userResponse, register] = useResource(({ username, password }) => ({
    url: "/users",
    method: "post",
    data: { email: username, password },
  }));

  useEffect(() => {
    if (userResponse.isLoading === false && userResponse.data) {
      dispatch({ type: "REGISTER", username: userResponse.data.email });
    }
    // Handle errors if there are any
    if (userResponse.error) {
      alert("Registration failed: " + userResponse.error.message);
    }
  }, [userResponse, dispatch]);

  function handleUsername(evt) {
    setUsername(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handlePasswordRepeat(evt) {
    setPasswordRepeat(evt.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (password !== passwordRepeat) {
      alert("Passwords do not match");
      return;
    }
    register({ username, password });
  }

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="register-username">Username (Email):</label>
      <input
        type="text"
        value={username}
        onChange={handleUsername}
        name="register-username"
        id="register-username"
      />

      <label htmlFor="register-password">Password:</label>
      <input
        type="password"
        value={password}
        onChange={handlePassword}
        name="register-password"
        id="register-password"
      />

      <label htmlFor="register-password-repeat">Repeat password:</label>
      <input
        type="password"
        value={passwordRepeat}
        onChange={handlePasswordRepeat}
        name="register-password-repeat"
        id="register-password-repeat"
      />

      <input
        type="submit"
        value="Register"
        disabled={
          username.length === 0 ||
          password.length === 0 ||
          password !== passwordRepeat
        }
      />
    </form>
  );
}
