import React, { useState, useContext, useEffect } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";

export default function Login() {
  const { dispatch } = useContext(StateContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Define the useResource hooks
  const [userResult, login] = useResource((username, password) => ({
    url: "http://localhost:4000/users",
    method: "get",
    params: { username, password },
  }));
  const [registerResult, register] = useResource((username, password) => ({
    url: "http://localhost:4000/users",
    method: "post",
    data: { username, password },
  }));

  // Effect for handling login response
  useEffect(() => {
    if (userResult.data) {
      if (userResult.data.length > 0) {
        // User found, handle the successful login
        dispatch({ type: "LOGIN", username });
      } else {
        // User not found, attempt to register
        register(username, password);
      }
    }
    // Handle login error if necessary
    if (userResult.error) {
      // Log the error or display a message to the user
    }
  }, [userResult, username, password, dispatch, register]);

  // Effect for handling registration response
  useEffect(() => {
    if (registerResult.data) {
      // Handle the successful registration
      dispatch({ type: "REGISTER", username });
      dispatch({ type: "LOGIN", username });
    }
    // Handle registration error if necessary
    if (registerResult.error) {
      // Log the error or display a message to the user
    }
  }, [registerResult, username, dispatch]);

  function handleUsername(evt) {
    setUsername(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handleLogin(e) {
    e.preventDefault();
    login(username, password);
  }

  return (
    <form onSubmit={handleLogin}>
      <label htmlFor="login-username">Username:</label>
      <input
        type="text"
        name="login-username"
        id="login-username"
        value={username}
        onChange={handleUsername}
      />
      <label htmlFor="login-password">Password:</label>
      <input
        type="password"
        name="login-password"
        id="login-password"
        value={password}
        onChange={handlePassword}
      />
      <input
        type="submit"
        value="Login"
        disabled={username.length === 0 || password.length === 0}
      />
    </form>
  );
}
