import { useState, useEffect } from "react";
import { useResource } from "react-request-hook";

export default function Register({ dispatch }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [passwordRepeat, setPasswordRepeat] = useState("");

  // Define the useResource hook for registration
  const [userResult, register] = useResource(({ username, password }) => ({
    url: "http://localhost:4000/users", // Your registration endpoint
    method: "post",
    data: { username, password },
  }));

  // Listen for changes in the registration response
  useEffect(() => {
    if (userResult && userResult.data) {
      // Dispatch the REGISTER_SUCCESS action if the registration was successful
      dispatch({ type: "REGISTER_SUCCESS", user: userResult.data });
    }
    // Add error handling here if needed
  }, [userResult, dispatch]);

  function handleUsername(evt) {
    setUsername(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handlePasswordRepeat(evt) {
    setPasswordRepeat(evt.target.value);
  }

  function handleRegister(evt) {
    evt.preventDefault();
    if (password !== passwordRepeat) {
      // Handle the password mismatch scenario
      console.error("Passwords do not match!");
      return;
    }
    register({ username, password }); // Call the register function from useResource
  }

  return (
    <form onSubmit={handleRegister}>
      <label htmlFor="register-username">Username:</label>
      <input
        type="text"
        value={username}
        onChange={handleUsername}
        name="register-username"
        id="register-username"
      />

      <label htmlFor="register-password">Password:</label>
      <input
        type="password"
        value={password}
        onChange={handlePassword}
        name="register-password"
        id="register-password"
      />

      <label htmlFor="register-password-repeat">Repeat password:</label>
      <input
        type="password"
        value={passwordRepeat}
        onChange={handlePasswordRepeat}
        name="register-password-repeat"
        id="register-password-repeat"
      />

      <input
        type="submit"
        value="Register"
        disabled={
          username.length === 0 ||
          password.length === 0 ||
          password !== passwordRepeat
        }
      />
    </form>
  );
}
