import React, { useState, useContext, useEffect } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";

export default function Login() {
  const { dispatch } = useContext(StateContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const [loginResult, login] = useResource((username, password) => ({
    url: "/users",
    method: "post",
    data: { username, password },
  }));

  const [registerResult, register] = useResource((username, password) => ({
    url: "/users",
    method: "post",
    data: { username, password },
  }));

  // When the component mounts, set up the login and register effects
  useEffect(() => {
    if (loginResult.data) {
      // Handle login success
      dispatch({ type: "LOGIN", username });
    } else if (loginResult.error) {
      // If login fails, attempt to register
      register(username, password);
    }
  }, [loginResult, username, password, dispatch, register]);

  useEffect(() => {
    if (registerResult.data) {
      // Handle registration success
      dispatch({ type: "REGISTER", username });
      dispatch({ type: "LOGIN", username });
    }
    // Optionally handle registration error
  }, [registerResult, username, dispatch]);

  function handleUsername(evt) {
    setUsername(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    login(username, password);
  }

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="login-username">Username:</label>
      <input
        type="text"
        name="login-username"
        id="login-username"
        value={username}
        onChange={handleUsername}
      />
      <label htmlFor="login-password">Password:</label>
      <input
        type="password"
        name="login-password"
        id="login-password"
        value={password}
        onChange={handlePassword}
      />
      <input
        type="submit"
        value="Login"
        disabled={username.length === 0 || password.length === 0}
      />
    </form>
  );
}
