import React, { useState, useContext, useEffect } from "react";
import { useResource } from "react-request-hook";
import { StateContext } from "./contexts";

export default function Login() {
  const { dispatch } = useContext(StateContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Define useResource hook for login
  const [loginResponse, login] = useResource((email, password) => ({
    url: "/users", // The correct endpoint for login, if it differs, change accordingly
    method: "post",
    data: { email, password },
  }));

  function handleEmail(evt) {
    setEmail(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    login(email, password);
  }

  // Handle login response
  useEffect(() => {
    if (loginResponse && loginResponse.data) {
      // Assuming login response includes username or email to save in context
      dispatch({ type: "LOGIN", username: loginResponse.data.email });
    }
    // Optionally handle errors here
  }, [loginResponse, dispatch]);

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="login-email">Email:</label>
      <input
        type="email"
        name="login-email"
        id="login-email"
        value={email}
        onChange={handleEmail}
      />
      <label htmlFor="login-password">Password:</label>
      <input
        type="password"
        name="login-password"
        id="login-password"
        value={password}
        onChange={handlePassword}
      />
      <input
        type="submit"
        value="Login"
        disabled={email.length === 0 || password.length === 0}
      />
    </form>
  );
}
