import React, { useState, useEffect } from "react"; // import useEffect here
import { useResource } from "react-request-hook";

export default function Register({ dispatch, setIsLoggedIn }) {
  // formData state initialization
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    passwordRepeat: "",
  });

  // Define the useResource hook for registration
  const [user, register] = useResource(({ username, password }) => ({
    url: "http://localhost:4000/users", // Adjust if you have a different endpoint for registration
    method: "post",
    data: { username, password }, // Your API's expected format for registration
  }));

  useEffect(() => {
    if (user && user.data) {
      // Dispatch the REGISTER action if the registration was successful
      dispatch({ type: "REGISTER", username: user.data.username });
      setIsLoggedIn(true);
    }
    // You can add error handling here based on user.error if needed
  }, [user, dispatch, setIsLoggedIn]);

  function handleUsername(evt) {
    setFormData({ ...formData, username: evt.target.value });
  }

  function handlePassword(evt) {
    setFormData({ ...formData, password: evt.target.value });
  }

  function handlePasswordRepeat(evt) {
    setFormData({ ...formData, passwordRepeat: evt.target.value });
  }

  function handleRegister(evt) {
    evt.preventDefault();
    if (formData.password !== formData.passwordRepeat) {
      alert("Passwords do not match!");
      return;
    }
    // Call the API to register the user
    register({ username: formData.username, password: formData.password });
  }

  return (
    <form onSubmit={handleRegister}>
      <label htmlFor="register-username">Username:</label>
      <input
        type="text"
        name="register-username"
        id="register-username"
        value={formData.username}
        onChange={handleUsername}
      />

      <label htmlFor="register-password">Password:</label>
      <input
        type="password"
        name="register-password"
        id="register-password"
        value={formData.password}
        onChange={handlePassword}
      />

      <label htmlFor="register-password-repeat">Repeat Password:</label>
      <input
        type="password"
        name="register-password-repeat"
        id="register-password-repeat"
        value={formData.passwordRepeat}
        onChange={handlePasswordRepeat}
      />

      <input
        type="submit"
        value="Register"
        disabled={
          !formData.username ||
          !formData.password ||
          formData.password !== formData.passwordRepeat
        }
      />
    </form>
  );
}
