import React, { useContext, useEffect, useState } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";
import "./Todo.css";

export default function Todo({
  id,
  title,
  description,
  dateCreated,
  complete,
  dateCompleted,
}) {
  const { state, dispatch } = useContext(StateContext);

  const [updatedDateCompleted, setUpdatedDateCompleted] =
    useState(dateCompleted);

  const [toggleTodoState, toggleTodo] = useResource(
    ({ id, complete, dateCompleted }) => ({
      url: `/todo/${id}`, // Assuming you have a route to toggle the todo
      method: "patch",
      headers: {
        Authorization: `${state.user.access_token}`,
      },
      data: { complete, dateCompleted },
    })
  );

  const [, deleteTodo] = useResource(({ id }) => ({
    url: `/todo/delete`, // General URL without the id
    method: "delete",
    headers: {
      Authorization: `${state.user.access_token}`,
    },
    data: { id }, // Send id in the request body
  }));

  const formatDateAndTime = (timestamp) => {
    const date = new Date(timestamp);
    return `${
      date.getMonth() + 1
    }/${date.getDate()}/${date.getFullYear()} ${date.getHours()}:${
      date.getMinutes() < 10 ? "0" : ""
    }${date.getMinutes()}`;
  };

  const handleToggle = () => {
    const newDateCompleted = !complete ? new Date().toISOString() : null;
    setUpdatedDateCompleted(newDateCompleted);
    toggleTodo({ id, complete: !complete, dateCompleted: newDateCompleted });
  };

  const handleDelete = () => {
    deleteTodo({ id }).then(() => {
      dispatch({ type: "DELETE_TODO", id });
    });
  };

  useEffect(() => {
    if (toggleTodoState && toggleTodoState.data) {
      // Request was successful, dispatch the action
      dispatch({
        type: "UPDATE_TODO",
        id: toggleTodoState.data._id,
        title: toggleTodoState.data.title,
        description: toggleTodoState.data.description,
      });
    }
  }, [toggleTodoState, dispatch]);

  return (
    <div className="todo-item">
      <div className="todo-title">{title}</div>
      <div className="todo-description"> Description: {description}</div>
      <div className="todo-details">
        <div>Created: {formatDateAndTime(dateCreated)}</div>
        <br />
        <div>
          Completed:{" "}
          {updatedDateCompleted
            ? formatDateAndTime(updatedDateCompleted)
            : "Not completed"}
        </div>
        <br />
        <i>Written by {state.user.username}</i>
      </div>
      <div className="todo-checkbox-wrapper">
        <input type="checkbox" checked={complete} onChange={handleToggle} />
        <label className="todo-checkbox-label">Complete</label>
        <br />
      </div>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
}
