import React, { useState, useContext, useEffect } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";

export default function Login() {
  const { dispatch } = useContext(StateContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Define the useResource hook for the login POST request
  const [userLogin, login] = useResource((email, password) => ({
    url: "http://localhost:4000/login", // Adjust this URL to your backend endpoint that handles login
    method: "post",
    data: { email, password },
  }));

  function handleEmail(evt) {
    setEmail(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    login(email, password);
  }

  // Handle login response
  useEffect(() => {
    if (userLogin && userLogin.data) {
      // This is where you might want to dispatch a login success action
      dispatch({ type: "LOGIN", username: userLogin.data.email }); // Assuming email is used as the username
    }
  }, [userLogin, dispatch]);

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="login-email">Email:</label>
      <input
        type="email"
        name="login-email"
        id="login-email"
        value={email}
        onChange={handleEmail}
      />
      <label htmlFor="login-password">Password:</label>
      <input
        type="password"
        name="login-password"
        id="login-password"
        value={password}
        onChange={handlePassword}
      />
      <input type="submit" value="Login" disabled={email.length === 0} />
    </form>
  );
}
