import React, { useContext } from "react";
import { StateContext } from "./contexts"; 
import { useResource } from "react-request-hook";
import { useEffect } from "react";
import "./Todo.css";


export default function Todo({
  id,
  title,
  description,
  dateCreated,
  author,
  complete,
  dateCompleted,
}) {
  const { dispatch } = useContext(StateContext);
 const [toggleResponse, toggleTodo] = useResource(({ id, complete }) => ({
   url: `/posts/${id}`,
   method: "patch",
   data: { complete },
 }));

 const [, deleteTodo] = useResource((id) => ({
   url: `/posts/${id}`,
   method: "delete",
 }));
  const formatDateAndTime = (timestamp) => {
    const date = new Date(timestamp);
    return `${
      date.getMonth() + 1
    }/${date.getDate()}/${date.getFullYear()} ${date.getHours()}:${
      date.getMinutes() < 10 ? "0" : ""
    }${date.getMinutes()}`;
  };
 const handleToggle = () => {
   const newCompleteStatus = !complete;
   toggleTodo({
     id,
     complete: newCompleteStatus,
   });
 };

   useEffect(() => {
     if (toggleResponse && toggleResponse.data) {
       dispatch({
         type: "TOGGLE_TODO",
         id,
         dateCompleted: toggleResponse.data.dateCompleted,
       });
     }
   }, [id,toggleResponse, dispatch]);

// Modify the onClick handler for the delete button
const handleDelete = () => {
  deleteTodo(id);
  dispatch({ type: "DELETE_TODO", id });
};


  return (
    <div className="todo-item">
      <div className="todo-title">{title}</div>
      <div className="todo-description"> Description: {description}</div>
      <div className="todo-details">
        <div>Created: {formatDateAndTime(dateCreated)}</div>
        <br />
        <div>
          Completed:{" "}
          {dateCompleted ? formatDateAndTime(dateCompleted) : "Not completed"}
        </div>
        <br />
        <i>Written by {author}</i>
      </div>
      <div className="todo-checkbox-wrapper">
        <input type="checkbox" checked={complete} onChange={handleToggle} />
        <label className="todo-checkbox-label">Complete</label>
        <br />
      </div>
     <button onClick={handleDelete}>
        Delete
      </button>
    </div>
  );
}
