import { useReducer, useEffect } from "react";
import { useResource } from "react-request-hook";
import CreateTodo from "./CreateTodo";
import UserBar from "./UserBar";
import TodoList from "./TodoList";
import { userReducer, todoReducer } from "./reducers";
import "./App.css";

function App() {
  const [user, userDispatch] = useReducer(userReducer, "");
  const [todos, todosDispatch] = useReducer(todoReducer, []);

  // Define the useResource Hook for fetching todos
  const [todosResult, getTodos] = useResource(() => ({
    url: "/todos",
    method: "get",
  }));

  // Fetch todos when the component mounts
  useEffect(() => {
    getTodos();
  }, []); // Empty array ensures this only runs once on mount

  // Dispatch an action when todos are fetched
  useEffect(() => {
    if (todosResult && todosResult.data) {
      todosDispatch({ type: "FETCH_TODOS", todos: todosResult.data });
    }
  }, [todosResult]);

  return (
    <div className="App">
      <UserBar user={user} dispatch={userDispatch} />
      <CreateTodo user={user} dispatch={todosDispatch} />
      <TodoList todos={todos} dispatch={todosDispatch} />
    </div>
  );
}

export default App;
