import React, { useContext } from "react";
import { StateContext } from "./contexts"; 
import { useResource } from "react-request-hook";
import { useEffect } from "react";
import "./Todo.css";


export default function Todo({
  id,
  title,
  description,
  dateCreated,
  author,
  complete,
  dateCompleted,
}) {
  const { state, dispatch } = useContext(StateContext);



const [toggleTodoState, toggleTodo]  = useResource(({ id, complete, dateCompleted }) => ({
  url: `/todo/toggle`, // General URL without the id
  method: "patch",
  headers: {
    Authorization: `${state.user.access_token}`,
  },
  data: { id, complete, dateCompleted }, // Send id in the request body along with other data
}));



const [, deleteTodo] = useResource(({ id }) => ({
  url: `/todo/delete`, // General URL without the id
  method: "delete",
  headers: {
    Authorization: `${state.user.access_token}`,
  },
  data: { id }, // Send id in the request body
}));



  const formatDateAndTime = (timestamp) => {
    const date = new Date(timestamp);
    return `${
      date.getMonth() + 1
    }/${date.getDate()}/${date.getFullYear()} ${date.getHours()}:${
      date.getMinutes() < 10 ? "0" : ""
    }${date.getMinutes()}`;
  };

const handleToggle = () => {
  const updatedDateCompleted = !complete ? new Date().toISOString() : null;
  toggleTodo({ id, complete: !complete, dateCompleted: updatedDateCompleted });
};


const handleDelete = () => {
  deleteTodo({ id }).then(() => {
    dispatch({ type: "DELETE_TODO", id });
  });
};

useEffect(() => {
  if (toggleTodoState && toggleTodoState.data) {
    // Request was successful, dispatch the action
    dispatch({
      type: "TOGGLE_TODO",
      id,
      complete: !complete,
      dateCompleted: updatedDateCompleted,
    });
  }
  // Add other necessary dependencies in the dependency array
}, [toggleTodoState, dispatch, id, complete, updatedDateCompleted]);  



  return (
    <div className="todo-item">
      <div className="todo-title">{title}</div>
      <div className="todo-description"> Description: {description}</div>
      <div className="todo-details">
        <div>Created: {formatDateAndTime(dateCreated)}</div>
        <br />
        <div>
          Completed:{" "}
          {dateCompleted ? formatDateAndTime(dateCompleted) : "Not completed"}
        </div>
        <br />
        <i>Written by {state.user.username}</i>
      </div>
      <div className="todo-checkbox-wrapper">
        <input type="checkbox" checked={complete} onChange={handleToggle} />
        <label className="todo-checkbox-label">Complete</label>
        <br />
      </div>
     <button onClick={handleDelete}>
        Delete
      </button>
    </div>
  );
}
