import React, { useState, useContext } from "react";
import { StateContext } from "./contexts";
import { useResource } from "react-request-hook";

export default function Login() {
  const { dispatch } = useContext(StateContext);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Define the useResource hook for the login POST request
  const [userLogin, login] = useResource((username, password) => ({
    url: "/login", // Adjust this URL to your API endpoint
    method: "post",
    data: { username, password },
  }));

  function handleUsername(evt) {
    setUsername(evt.target.value);
  }

  function handlePassword(evt) {
    setPassword(evt.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    login(username, password);
  }

  // Handle login response
  useEffect(() => {
    if (userLogin && userLogin.data) {
      // This is where you might want to dispatch a login success action
      dispatch({ type: "LOGIN", username: userLogin.data.username });
    }
  }, [userLogin, dispatch]);

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="login-username">Username:</label>
      <input
        type="text"
        name="login-username"
        id="login-username"
        value={username}
        onChange={handleUsername}
      />
      <label htmlFor="login-password">Password:</label>
      <input
        type="password"
        name="login-password"
        id="login-password"
        value={password}
        onChange={handlePassword}
      />
      <input type="submit" value="Login" disabled={username.length === 0} />
    </form>
  );
}
